# -*- coding: utf-8 -*-import threadingimport timefrom pynput import keyboard, mouseimport pyautogui# Varivel para controlar o estado de clique automticois_clicking = False# Funo para simular o clique contnuodef hold_left_click():    while is_clicking:        pyautogui.mouseDown()  # Segura o boto esquerdo do mouse        time.sleep(0.1)  # Pequena pausa para evitar uso excessivo de CPU        # Funo para gerenciar as teclas do tecladodef on_press(key):    global is_clicking    if key == keyboard.Key.delete:  # Ativa/desativa o clique ao pressionar "Delete"        is_clicking = not is_clicking        if is_clicking:            print("Clique continuo ativado!")            # Inicia a thread do clique contnuo            threading.Thread(target=hold_left_click, daemon=True).start()        else:            print("Clique continuo desativado!")            pyautogui.mouseUp()  # Libera o boto esquerdo do mouse# Listener do tecladowith keyboard.Listener(on_press=on_press) as listener:    print("Pressione 'Delete' para ativar/desativar o clique continuo.")    listener.join()